pragma solidity ^0.5.0; 

contract Uc {
    string public name = 'Uc';
    string public symbol = 'USD'; 
    uint256 public totalSupply = 1000000000000000000000000; // 1 million tokens
    uint8 public decimals = 18; 
    address public minter; 
    mapping(address=>uint) public balances; 


    event Sent(address from, address to, uint amount); 
    //error insufficientFunds(uint requested, uint avaiable); 

    constructor() public {
        minter = msg.sender; 
    }

    function mint(address receiver, uint amount) public {
        if (minter == msg.sender) {
            balances[receiver] += amount; 
        }
    }

    function send(address receiver, uint amount) public {
      /*  if (balances[msg.sender] < amount) {
            revert insufficientFunds({
                requested: amount, 
                avaiable: balances[msg.sender]
            }); 
        }
       */ 
        balances[msg.sender] -= amount;
        balances[receiver] += amount; 
        emit Sent(msg.sender, receiver, amount); 
    }


    function getR(address AccHolder) public view returns(uint){
        return balances[AccHolder]; 
    }


}
